import { IIssueTransaction, IIssueParams, WithId, WithSender } from '../transactions';
import { TSeedTypes } from '../types';
/**
 * Creates and signs [[IIssueTransaction]].
 *
 * You can use this function with multiple seeds. In this case it will sign transaction accordingly and will add one proof per seed.
 * Also you can use already signed [[IIssueTransaction]] as a second argument.
 *
 * ### Usage
 * ```js
 * const { issue } = require('waves-transactions')
 *
 * const seed = 'b716885e9ba64442b4f1263c8e2d8671e98b800c60ec4dc2a27c83e5f9002b18'
 *
 * const params = {
 *   name: 'SCAM TOKEN',
 *   description: 'Awesome token that will tokenize tokenization tokenized',
 *   quantity: 1000000,
 *   //reissuable: false
 *   //decimals: 8
 *   //timestamp: Date.now(),
 *   //fee: 100000000,
 *   //chainId: 'W'
 * }
 *
 * const signedIssueTx = issue(params, seed)
 * ```
 * ### Output
 * ```json
 * {
 *   "id": "6uhqy1Lkcbh8yckGBXgHRf4mAa2fWbgRqpXa6RPWDPEm",
 *   "type": 3,
 *   "version": 2,
 *   "senderPublicKey": "G62H1XE5rnaCgCCURV5pWwQHzWezZB7VkkVgqthdKgkj",
 *   "name": "SCAM TOKEN",
 *   "description": "Awesome token that will tokenize tokenization tokenized",
 *   "quantity": 1000000,
 *   "decimals": 8,
 *   "reissuable": false,
 *   "fee": 100000000,
 *   "timestamp": 1547021917068,
 *   "chainId": 87,
 *   "proofs": [
 *     "2Zfim2jC5Q4yqgQKVwdRi5xgkv9H6KbfuZ8G7P3NXAicAoxSSuKgFWhEm3DzZBaPH7BhommMqAi7moNoRKvJ3xbT"
 *   ]
 * }
 * ```
 *
 */
export declare function issue(params: IIssueParams, seed: TSeedTypes): IIssueTransaction & WithId;
export declare function issue(paramsOrTx: IIssueParams & WithSender | IIssueTransaction, seed?: TSeedTypes): IIssueTransaction & WithId;
